mapper.insert:
INSERT INTO auth_info
(
    ph_num
  , auth_code
)
VALUES
(
    #{phNum}
  , #{authCode}
)

mapper.update:
UPDATE auth_info
SET auth_code = #{authCode}
WHERE ph_num = #{phNum}

UPDATE user_info
SET login_dtm = now()
WHERE ph_num = #{phNum}

mapper.insert:
INSERT INTO user_info
